if(not IsAddOnLoaded('Blizzard_TimeManager')) then	LoadAddOn('Blizzard_TimeManager')endSetCVar('showClock', '1', 'SHOW_CLOCK')pMinimapToggleClock('1')TimeManagerClockButton:ClearAllPoints()if(pMinimapDB.coords) then	TimeManagerClockButton:SetPoint('BOTTOMLEFT', Minimap)else	TimeManagerClockButton:SetPoint('BOTTOM', Minimap)endTimeManagerClockButton:SetWidth(40)TimeManagerClockButton:SetHeight(14)TimeManagerClockButton:GetRegions():Hide()TimeManagerClockTicker:SetPoint('CENTER', TimeManagerClockButton)TimeManagerClockTicker:SetFont(pMinimapDB.font, pMinimapDB.fontsize, pMinimapDB.fontflag == 'NONE' and nil or pMinimapDB.fontflag)TimeManagerAlarmFiredTexture.Show = function() TimeManagerClockTicker:SetTextColor(1, 0, 0) endTimeManagerAlarmFiredTexture.Hide = function() TimeManagerClockTicker:SetTextColor(1, 1, 1) endTimeManagerClockButton:SetScript('OnClick', function(self, button)	if(self.alarmFiring) then		PlaySound('igMainMenuQuit')		TimeManager_TurnOffAlarm()	else		if(button == 'RightButton') then			if(not IsAddOnLoaded('Blizzard_Calendar')) then				LoadAddOn('Blizzard_Calendar')			end			ToggleCalendar()		else			ToggleTimeManager()		end	endend)