MiniMapCoordButton = CreateFrame('Button', 'MiniMapCoord', Minimap)MiniMapCoordButton:SetWidth(40)MiniMapCoordButton:SetHeight(14)MiniMapCoordButton:Show()MiniMapCoordButton:RegisterForClicks('AnyUp')MiniMapCoordButton:SetScript('OnClick', function() ToggleFrame(WorldMapFrame) end)if(pMinimapDB2.clock) then	MiniMapCoordButton:SetPoint('BOTTOMRIGHT', Minimap)else	MiniMapCoordButton:SetPoint('BOTTOM', Minimap)endMiniMapCoordText = MiniMapCoordButton:CreateFontString('MiniMapCoordText', 'OVERLAY')MiniMapCoordText:SetPoint('CENTER', MiniMapCoordButton)MiniMapCoordText:SetFont(pMinimapDB2.font, pMinimapDB2.fontsize, pMinimapDB2.fontflag)MiniMapCoordText:SetTextColor(1, 1, 1)local total = 0MiniMapCoordButton:SetScript('OnUpdate', function(self, elapsed)	total = total + elapsed	if(total > 0.25) then		if(IsInInstance()) then			MiniMapCoordText:SetText()		else			local x, y = GetPlayerMapPosition('player')			MiniMapCoordText:SetFormattedText('%.0f,%.0f', x*100, y*100)		end		total = 0	endend)MiniMapCoordButton:RegisterEvent('ZONE_CHANGED_NEW_AREA')MiniMapCoordButton:SetScript('OnEvent', function()	SetMapToCurrentZone()end)