local total, font, size = 0, MiniMapMailText:GetFont()MiniMapCoord = CreateFrame('Frame', 'MiniMapCoord', Minimap)MiniMapCoord:SetWidth(40)MiniMapCoord:SetHeight(14)MiniMapCoord:Show()if(GetCVar('showClock') == '1') then	MiniMapCoord:SetPoint('BOTTOMRIGHT', Minimap)else	MiniMapCoord:SetPoint('BOTTOM', Minimap)endMiniMapCoordText = MiniMapCoord:CreateFontString('MiniMapCoordText', 'OVERLAY')MiniMapCoordText:SetPoint('CENTER', MiniMapCoord)MiniMapCoordText:SetFont(font, size, 'OUTLINE')MiniMapCoordText:SetTextColor(1, 1, 1)MiniMapCoord:SetScript('OnUpdate', function(self, elapsed)	total = total + elapsed	if(total > 0.25) then		if(IsInInstance()) then			MiniMapCoordText:SetText()		else			local x, y = GetPlayerMapPosition('player')			MiniMapCoordText:SetFormattedText('%.0f,%.0f', x*100, y*100)		end		total = 0	endend)	TimeManagerClockButton:ClearAllPoints()TimeManagerClockButton:SetPoint('BOTTOMLEFT', Minimap)TimeManagerClockButton.SetPoint = function() endlocal handler = CreateFrame('Frame')handler:RegisterEvent('ZONE_CHANGED_NEW_AREA')handler:SetScript('OnEvent', function()	SetMapToCurrentZone()end)