MiniMapCoords = CreateFrame('Frame', 'MiniMapCoord', Minimap)MiniMapCoords:SetWidth(40)MiniMapCoords:SetHeight(14)MiniMapCoords:Show()if(pMinimapDB2.clock) then	MiniMapCoords:SetPoint('BOTTOMRIGHT', Minimap)else	MiniMapCoords:SetPoint('BOTTOM', Minimap)endMiniMapCoordText = MiniMapCoords:CreateFontString('MiniMapCoordText', 'OVERLAY')MiniMapCoordText:SetPoint('CENTER', MiniMapCoords)MiniMapCoordText:SetFont(pMinimapDB2.font, pMinimapDB2.fontsize, pMinimapDB2.fontflag)MiniMapCoordText:SetTextColor(1, 1, 1)local total = 0MiniMapCoord:SetScript('OnUpdate', function(self, elapsed)	total = total + elapsed	if(total > 0.25) then		if(IsInInstance()) then			MiniMapCoordText:SetText()		else			local x, y = GetPlayerMapPosition('player')			MiniMapCoordText:SetFormattedText('%.0f,%.0f', x*100, y*100)		end		total = 0	endend)local handler = CreateFrame('Frame')handler:RegisterEvent('ZONE_CHANGED_NEW_AREA')handler:SetScript('OnEvent', function()	SetMapToCurrentZone()end)